!< StringiFor `capitalize` test.
program capitalize
!-----------------------------------------------------------------------------------------------------------------------------------
!< StringiFor `capitalize` test.
!-----------------------------------------------------------------------------------------------------------------------------------
use, intrinsic :: iso_fortran_env, only : stdout => output_unit
use stringifor, only : string
!-----------------------------------------------------------------------------------------------------------------------------------

!-----------------------------------------------------------------------------------------------------------------------------------
implicit none
type(string) :: astring        !< A string.
logical      :: test_passed(1) !< List of passed tests.
!-----------------------------------------------------------------------------------------------------------------------------------

!-----------------------------------------------------------------------------------------------------------------------------------
test_passed = .false.

astring = 'say all Hello WorLD!'
test_passed(1) = astring%capitalize()//''=='Say all hello world!'
write(stdout, "(A)") 'Original: "'//astring//'"'
write(stdout, "(A)") 'Capitalize: "'//astring%capitalize()//'"'

write(stdout, "(A,L1)") new_line('a')//'Are all tests passed? ', all(test_passed)
stop
!-----------------------------------------------------------------------------------------------------------------------------------
endprogram capitalize
